import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-linkedin-badge',
  templateUrl: './linkedin-badge.component.html',
  styleUrls: ['./linkedin-badge.component.css']
})
export class LinkedinBadgeComponent implements OnInit {

  ngOnInit() {
    const script = document.createElement('script');
    script.src = 'https://platform.linkedin.com/badges/js/profile.js';
    script.async = true;
    script.defer = true;
    document.head.appendChild(script);
  }


}
<div class="badge-base LI-profile-badge"
     data-locale="en_US"
     data-size="large"
     data-theme="dark"
     data-type="VERTICAL"
     data-vanity="tanmayverma24"
     data-version="v1">
    <a class="badge-base__link LI-simple-link"
       href="https://ca.linkedin.com/in/tanmayverma24?trk=profile-badge">
       Tanmay Verma
    </a>
</div>
